function formSubmitFunc(i){return function(e){e.preventDefault(),e.stopPropagation(),e.returnValue=!1;var t=new XMLHttpRequest,r=document.getElementById(i.getAttribute("id")+"-status");r.classList.add("hide"),t.addEventListener("load",function(e){200==t.status?r.innerHTML="<p>Form submitted!</p>":r.innerHTML="<p>Error occurred while processing form.</p><p>"+t.responseText+"</p>",r.classList.remove("hide")}),t.addEventListener("error",function(e){r.innerHTML="<p>A server error occurred. <em>The form was not submitted.</em> Please try again later.</p>",r.classList.remove("hide")}),t.open("POST",i.getAttribute("action"));var n=new FormData(i);return t.send(n),!1}}for(var forms=document.getElementsByTagName("form"),i=0;i<forms.length;i++){var form=forms[i],submit=document.getElementById(form.getAttribute("id")+"-submit");submit.addEventListener("click",formSubmitFunc(form))}
